function alertResult(e){AlipayJSBridge.call("alert",{title:"结果",message:JSON.stringify(e)})}function init(){var e=document.getElementById("toastItem");e.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("toast",{content:"Toast测试",type:"success",duration:3e3},function(){console.log("toast消失后执行")})});var t=document.getElementById("alertItem");t.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("alert",{title:"亲",message:"你好",button:"确定"},function(){console.log("alert点击button后执行")})});var n=document.getElementById("pushWindowItem");n.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("pushWindow",{url:"./detail.html",param:{readTitle:!0,defaultTitle:!0,showOptionMenu:!1}})});var a=document.getElementById("citySelectItem");a.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("getCities",{currentCity:"杭州市",adcode:"310100"},function(e){alertResult(JSON.stringify(e))})});var i=document.getElementById("albumItem");i.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("photo",{dataType:"dataURL",imageFormat:"jpg",quality:75,maxWidth:500,maxHeight:500,allowEdit:!0},function(e){alertResult("base64: "+e.dataURL.slice(0,50)+"...")})});var l=document.getElementById("vibrateItem");l.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("vibrate")});var c=document.getElementById("scanItem");c.addEventListener("click",function(e){e.preventDefault(),AlipayJSBridge.call("scan",{type:"qr"},function(e){alertResult(e)})})}document.addEventListener("AlipayJSBridgeReady",function(){init()});